import './component.css';

const Textarea = (props) => { 

    let buttons = []; 
    let containerClassList = 'textarea '; 
    let headerClassList = '';

    props.content.buttons.map((button, index) => { 
        buttons.push(<a 
                        className={button.classList} 
                        href={button.link.destination} 
                        key={index}
                    >
                        {button.displayText}
                    </a>);
    });

    containerClassList += props.content.classList; 
    headerClassList += props.content.heading.classList;

    return (
        <div className={containerClassList}  data-hud="core/textarea">
            <h2 className={headerClassList}>{props.content.heading.text}</h2>
            <p className="has-mg-bottom-20">{props.content.textarea}</p> 
            {buttons}
        </div>
    );  
}

export default Textarea;

layout/threeColumn_Cards.jsx:
import './component.css';
import PriceTierCard from '../../core/priceTierCard/component';
import ContentContext from '../../../context/contentContext';
import { useContext } from "react";

const registeredCards = { 
    PriceTierCard: PriceTierCard
}

const ThreeColumn_Cards = (props) => {  
    const { content } = useContext( ContentContext ); 
    const cardContent = content[ props.parentComponent ][ 'threeColumn_Cards' ][ 'cards' ];

    let cards = [];
    const CardType = registeredCards[ props.cardType ]; 

    cardContent.map((card, index ) => {
        cards.push(
            <CardType content={card} key={index} />
        )
    });

    return (
        <div className="threeColumn_card-container display-flex"  data-hud="layout/threeColumn_Cards">
            {cards}
        </div>
    );  
}

export default ThreeColumn_Cards;

layout/twoColumn_HeadlineAndImage.jsx:
import './component.css';
import Textarea from '../../core/textarea/component'; 
import Image from '../../core/image/component';
import ContentContext from '../../../context/contentContext';
import { useContext } from "react";

const TwoColumn_HeadlineAndImage = (props) => {    
    const { content } = useContext( ContentContext ); 

    let imageColumnClassList = "v-col md-3 lg-4 left ";
    imageColumnClassList += content[ props.parentComponent ][ 'twoColumn_TextareaAndImage' ][ 'imageOrientation' ];

    const textareaContent = content[ props.parentComponent ][ 'twoColumn_TextareaAndImage' ][ 'textarea' ]; 
    const imageContent = content[ props.parentComponent ][ 'twoColumn_TextareaAndImage' ][ 'image' ]; 

    return (
        <div className="twoColumnHeadlineImage h-row clearfix" data-hud="layout/twoColumn_HeadlineAndImage">
            <div className={imageColumnClassList}>
                <Image content={ imageContent }/>
            </div>
            <div className="v-col md-3 lg-4 right">
                <Textarea content={ textareaContent }/>
            </div>
        </div>
    );  
}

export default TwoColumn_HeadlineAndImage;

